import React, { useState } from 'react';
import { View, Text, Image, TouchableOpacity, Modal, StyleSheet } from 'react-native';

const FavoritePage = () => {
    const [favorites, setFavorites] = useState([
        {
            id: 1,
            title: 'Coffee Mug',
            price: '₱15.99',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 2,
            title: 'Espresso Machine',
            price: '₱299.99',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 3,
            title: 'Shorts',
            price: '₱10.99',
            image: 'data:image/jpeg;base64,'
        },
        {
          id: 4,
          title: 'Dress',
          price: '₱10.99',
          image: 'data:image/jpeg;base64,',
        }
    ]);
    const [showModal, setShowModal] = useState(false);
    const [itemToRemove, setItemToRemove] = useState(null);

    const handleRemoveClick = (item) => {
        setItemToRemove(item);
        setShowModal(true);
    };

    const confirmRemove = () => {
        setFavorites(favorites.filter(item => item.id !== itemToRemove.id));
        setShowModal(false);
        setItemToRemove(null);
    };

    const cancelRemove = () => {
        setShowModal(false);
        setItemToRemove(null);
    };

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Favorites</Text>
            {favorites.length === 0 ? (
                <Text style={styles.emptyMessage}>You have no favorite items yet.</Text>
            ) : (
                <View style={styles.grid}>
                    {favorites.map(item => (
                        <View key={item.id} style={styles.card}>
                            <Image source={{ uri: item.image }} style={styles.image} />
                            <Text style={styles.itemTitle}>{item.title}</Text>
                            <Text style={styles.price}>{item.price}</Text>
                            <TouchableOpacity 
                                onPress={() => handleRemoveClick(item)} 
                                style={styles.removeButton}
                            >
                                <Text style={styles.removeButtonText}>Remove from Favorites</Text>
                            </TouchableOpacity>
                        </View>
                    ))}
                </View>
            )}

            {/* Confirmation Modal */}
            <Modal
                transparent={true}
                visible={showModal}
                animationType="slide"
            >
                <View style={styles.modalOverlay}>
                    <View style={styles.modalContent}>
                        <Text style={styles.modalTitle}>Confirm Removal</Text>
                        <Text>Are you sure you want to remove "{itemToRemove?.title}" from your favorites?</Text>
                        <View style={styles.modalButtons}>
                            <TouchableOpacity 
                                onPress={confirmRemove} 
                                style={styles.confirmButton}
                            >
                                <Text style={styles.buttonText}>Yes, Remove</Text>
                            </TouchableOpacity>
                            <TouchableOpacity 
                                onPress={cancelRemove} 
                                style={styles.cancelButton}
                            >
                                <Text style={styles.buttonText}>Cancel</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </View>
            </Modal>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
        backgroundColor: '#315a39',
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 16,
    },
    emptyMessage: {
        color: '#888',
        textAlign: 'center',
    },
    grid: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    },
    card: {
        backgroundColor: '#fff',
        borderRadius: 8,
        elevation: 2,
        marginBottom: 16,
        width: '48%',
        padding: 12,
    },
    image: {
        width: '100%',
        height: 100,
        borderRadius: 8,
        marginBottom: 8,
    },
    itemTitle: {
        fontSize: 18,
        fontWeight: '600',
    },
    price: {
        color: '#666',
        marginBottom: 8,
    },
    removeButton: {
        backgroundColor: '#e63946',
        padding: 10,
        borderRadius: 5,
    },
    removeButtonText: {
        color: '#fff',
        textAlign: 'center',
    },
    modalOverlay: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
    },
    modalContent: {
        backgroundColor: '#fff',
        borderRadius: 8,
        padding: 20,
        width: '80%',
        alignItems: 'center',
    },
    modalTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 12,
    },
    modalButtons: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '100%',
    },
    confirmButton: {
        backgroundColor: '#e63946',
        padding: 10,
        borderRadius: 5,
        marginRight: 10,
        flex: 1,
    },
    cancelButton: {
        backgroundColor: '#ccc',
        padding: 10,
        borderRadius: 5,
        flex: 1,
    },
    buttonText: {
        color: '#fff',
        textAlign: 'center',
    },
});

export default FavoritePage;
